def get_logindata(user, pswd, mode=True):
    if mode:
        data = {
            'username': '{}'.format(user),
            'password': '{}'.format(pswd)
        }
        return data
    else:
        data = {
            "username": "{}".format(user),
            "password": "{}".format(pswd),
            'execution': '228c85c3-5f9f-4a81-b78c-2ea36bcfcde9_',
            'submit': '登录',
            'type': 'username_password',
            '_eventId': 'submit'
        }
        return data


def get_log():
    with open('Running_log', 'r') as f:
        temp = f.readlines()
        print('log is {}'.format(temp))
        return temp
